mapper.select:
select
request_id, case_name, case_desc, element_name, element_phone, element_bank, element_idcard,
element_bank_mobile, test_data, createdatetime, updatetime
from auto_test_demo.scene_cases_pool


        and request_id = #{requestId}


        and case_name = #{caseName}


        and case_desc = #{caseDesc}


        and element_name = #{elementName}


        and element_phone = #{elementPhone}


        and element_bank = #{elementBank}


        and element_idcard = #{elementIdcard}


        and element_bank_mobile = #{elementBankMobile}


        and test_data = #{testData}


        and createdatetime = #{createdatetime}


        and updatetime = #{updatetime}
