mapper.select:
select
request_id, case_name, case_desc, prod_sub_no, prod_no, channel_into, status_conf, element_name, element_phone,
element_bank, element_idcard, element_bank_mobile, test_data, createdatetime, updatetime
from autotest.scene_cases_pool

    
        and request_id = #{requestId}
    
    
        and case_name = #{caseName}
    
    
        and case_desc = #{caseDesc}
    
    
        and prod_sub_no = #{prodSubNo}
    
    
        and prod_no = #{prodNo}
    
    
        and channel_into = #{channelInto}
    
    
        and status_conf = #{statusConf}
    
    
        and element_name = #{elementName}
    
    
        and element_phone = #{elementPhone}
    
    
        and element_bank = #{elementBank}
    
    
        and element_idcard = #{elementIdcard}
    
    
        and element_bank_mobile = #{elementBankMobile}
    
    
        and test_data = #{testData}
    
    
        and createdatetime = #{createdatetime}
    
    
        and updatetime = #{updatetime}
