mapper.select:
select
request_id, variable_code, variable_id, variable_value, variable_data_type, default_value, channel_into,
proc_map_id, create_date, update_date
from auto_test_demo.verification_pool


        and request_id = #{requestId}


        and variable_code = #{variableCode}


        and variable_id = #{variableId}


        and variable_value = #{variableValue}


        and variable_data_type = #{variableDataType}


        and default_value = #{defaultValue}


        and channel_into = #{channelInto}


        and proc_map_id = #{procMapId}


        and create_date = #{createDate}


        and update_date = #{updateDate}
